import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertContactSubmissionSchema } from "@shared/schema";
import { qualifyLead, chatWithAssistant, generateBlogIdea } from "./openai";

export async function registerRoutes(app: Express): Promise<Server> {
  // Contact Form Submission with AI Lead Qualification
  app.post("/api/qualify-lead", async (req, res) => {
    try {
      const { name, email, company, helpWith, message } = req.body;

      if (!name || !email || !helpWith) {
        return res.status(400).json({ 
          message: "Name, email, and help needed are required" 
        });
      }

      // CRITICAL: Save submission FIRST before attempting AI qualification
      const submission = await storage.createContactSubmission({
        name,
        email,
        company: company || null,
        helpWith,
        message: message || null,
        aiCategory: null,
        aiPriority: null,
        aiResponse: null,
      });

      // Try AI qualification as enhancement, but don't fail if it errors
      try {
        const qualification = await qualifyLead(name, email, company, helpWith, message);

        // Update submission with AI insights
        await storage.updateContactSubmission(submission.id, {
          aiCategory: qualification.category,
          aiPriority: qualification.priority,
          aiResponse: qualification.suggestedResponse,
        });

        return res.json({
          success: true,
          saved: true,
          qualification
        });
      } catch (aiError: any) {
        console.error("AI qualification failed, but submission was saved:", aiError);

        // Return success since submission is saved, but note AI is unavailable
        return res.json({
          success: true,
          saved: true,
          aiUnavailable: true,
          message: "Your inquiry was saved successfully. We'll respond within 24 hours."
        });
      }
    } catch (error: any) {
      console.error("Error processing contact submission:", error);
      res.status(500).json({ 
        message: "Failed to save your inquiry. Please try again or contact us directly.",
        error: error.message 
      });
    }
  });

  // AI Chatbot
  app.post("/api/chat", async (req, res) => {
    try {
      const { messages } = req.body;

      if (!messages || !Array.isArray(messages)) {
        return res.status(400).json({ 
          message: "Messages array is required" 
        });
      }

      const chatResponse = await chatWithAssistant(messages);

      res.json({
        success: true,
        ...chatResponse
      });
    } catch (error: any) {
      console.error("Error in chat:", error);

      // Differentiate rate limit errors from other errors
      if (error.status === 429 || error.code === 'insufficient_quota') {
        return res.status(503).json({ 
          message: "AI service temporarily unavailable",
          code: "SERVICE_UNAVAILABLE"
        });
      }

      res.status(500).json({ 
        message: "Failed to process chat",
        error: error.message 
      });
    }
  });

  // AI Blog Post Generator
  app.post("/api/generate-blog-idea", async (req, res) => {
    try {
      const { topic, category } = req.body;

      if (!topic) {
        return res.status(400).json({ 
          message: "Topic is required" 
        });
      }

      const blogIdea = await generateBlogIdea(
        topic, 
        category || "SaaS Growth"
      );

      res.json({
        success: true,
        blogIdea
      });
    } catch (error: any) {
      console.error("Error generating blog idea:", error);

      // Differentiate rate limit errors from other errors
      if (error.status === 429 || error.code === 'insufficient_quota') {
        return res.status(503).json({ 
          message: "AI content generator temporarily unavailable",
          code: "SERVICE_UNAVAILABLE"
        });
      }

      res.status(500).json({ 
        message: "Failed to generate blog idea",
        error: error.message 
      });
    }
  });

  const httpServer = createServer(app);

  return httpServer;
}

server/storage.ts.content:
import { 
  type User, 
  type InsertUser,
  type ContactSubmission,
  type InsertContactSubmission
} from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  createContactSubmission(submission: InsertContactSubmission): Promise<ContactSubmission>;
  updateContactSubmission(id: string, updates: Partial<ContactSubmission>): Promise<ContactSubmission | undefined>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private contactSubmissions: Map<string, ContactSubmission>;

  constructor() {
    this.users = new Map();
    this.contactSubmissions = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createContactSubmission(insertSubmission: InsertContactSubmission): Promise<ContactSubmission> {
    const id = randomUUID();
    const submission: ContactSubmission = {
      id,
      name: insertSubmission.name,
      email: insertSubmission.email,
      company: insertSubmission.company ?? null,
      helpWith: insertSubmission.helpWith,
      message: insertSubmission.message ?? null,
      aiCategory: insertSubmission.aiCategory ?? null,
      aiPriority: insertSubmission.aiPriority ?? null,
      aiResponse: insertSubmission.aiResponse ?? null,
      submittedAt: new Date(),
    };
    this.contactSubmissions.set(id, submission);
    return submission;
  }

  async updateContactSubmission(id: string, updates: Partial<ContactSubmission>): Promise<ContactSubmission | undefined> {
    const existing = this.contactSubmissions.get(id);
    if (!existing) return undefined;

    const updated = { ...existing, ...updates };
    this.contactSubmissions.set(id, updated);
    return updated;
  }
}

export const storage = new MemStorage();

