    <div className="min-h-screen w-full flex items-center justify-center bg-gray-50">
      <Card className="w-full max-w-md mx-4">
        <CardContent className="pt-6">
          <div className="flex mb-4 gap-2">
            <AlertCircle className="h-8 w-8 text-red-500" />
            <h1 className="text-2xl font-bold text-gray-900">404 Page Not Found</h1>
          </div>

          <p className="mt-4 text-sm text-gray-600">
            Did you forget to add the page to the router?
          </p>
        </CardContent>
      </Card>
    </div>
  );
}

components/ChatbotWidget.tsx.content:
'use client'

import { useState, useRef, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { MessageCircle, X, Send } from 'lucide-react';
import { useToast } from '@/lib/use-toast';

interface Message {
  role: 'user' | 'assistant';
  content: string;
}

export default function ChatbotWidget() {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState<Message[]>([
    {
      role: 'assistant',
      content: "Hi! I'm here to help you learn about Deltanoid's services. Ask me anything about SaaS development, performance marketing, or reputation management!"
    }
  ]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const { toast } = useToast();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || isLoading) return;

    const userMessage = input.trim();
    setInput('');

    const newMessages = [...messages, { role: 'user' as const, content: userMessage }];
    setMessages(newMessages);
    setIsLoading(true);

    try {
      const response = await fetch('/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ 
          message: userMessage,
          history: messages.slice(-6)
        })
      });

      const data = await response.json();

      if (!response.ok || !data.success) {
        throw new Error(data.message || 'Failed to get response');
      }

      if (data.reply) {
        setMessages([...newMessages, { role: 'assistant', content: data.reply }]);
      } else {
        throw new Error('No response from AI');
      }
    } catch (error) {
      console.error('Chat error:', error);
      setMessages([...newMessages, { 
        role: 'assistant', 
        content: "I'm having trouble connecting right now. Please try asking your question via our contact form on the Let's Talk page, or email us at admin@deltanoid.in"
      }]);
      toast({
        title: "Connection Issue",
        description: "The chatbot is temporarily unavailable. Please use our contact form instead.",
        variant: "destructive"
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <>
      {isOpen && (
        <div className="fixed inset-0 md:inset-auto md:bottom-24 md:right-6 md:w-96 md:h-[32rem] z-50">
          <Card className="h-full flex flex-col shadow-2xl">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4 border-b">
              <CardTitle className="text-lg font-semibold flex items-center gap-2">
                <MessageCircle className="w-5 h-5 text-primary" />
                Chat with Deltanoid AI
              </CardTitle>
              <Button
                variant="ghost"
                size="icon"
                onClick={() => setIsOpen(false)}
                aria-label="Close chat"
                data-testid="button-close-chat"
              >
                <X className="w-4 h-4" />
              </Button>
            </CardHeader>
            <CardContent className="flex-1 flex flex-col p-0">
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {messages.map((message, index) => (
                  <div
                    key={index}
                    className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div
                      className={`max-w-[80%] rounded-lg p-3 ${
                        message.role === 'user'
                          ? 'bg-primary text-primary-foreground'
                          : 'bg-muted text-foreground'
                      }`}
                      data-testid={`message-${message.role}-${index}`}
                    >
                      <p className="text-sm whitespace-pre-wrap break-words">{message.content}</p>
                    </div>
                  </div>
                ))}
                {isLoading && (
                  <div className="flex justify-start">
                    <div className="bg-muted rounded-lg p-3">
                      <p className="text-sm text-muted-foreground">Thinking...</p>
                    </div>
                  </div>
                )}
                <div ref={messagesEndRef} />
              </div>
              <div className="border-t p-4">
                <div className="flex gap-2">
                  <Input
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Ask about our services..."
                    disabled={isLoading}
                    data-testid="input-chat-message"
                  />
                  <Button
                    onClick={handleSend}
                    disabled={!input.trim() || isLoading}
                    size="icon"
                    aria-label="Send message"
                    data-testid="button-send-message"
                  >
                    <Send className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {!isOpen && (
        <Button
          onClick={() => setIsOpen(true)}
          size="icon"
          aria-label="Open chat"
          className="fixed bottom-6 right-6 z-50 h-14 w-14 rounded-full shadow-lg"
          data-testid="button-open-chat"
        >
          <MessageCircle className="w-6 h-6" />
        </Button>
      )}
    </>
  );
}

app/api/chat/route.ts.content:
import { NextResponse } from "next/server";
import { chatWithAssistant } from "@/server/openai";

export async function POST(req: Request) {
  try {
    const { messages } = await req.json();

    if (!messages || !Array.isArray(messages)) {
      return NextResponse.json(
        { message: "Messages array is required" },
        { status: 400 }
      );
    }

    const chatResponse = await chatWithAssistant(messages);

    return NextResponse.json({
      success: true,
      ...chatResponse
    });
  } catch (error: any) {
    console.error("Error in chat:", error);

    if (error.status === 429 || error.code === 'insufficient_quota') {
      return NextResponse.json(
        { 
          message: "AI service temporarily unavailable",
          code: "SERVICE_UNAVAILABLE"
        },
        { status: 503 }
      );
    }

    return NextResponse.json(
      { 
        message: "Failed to process chat",
        error: error.message 
      },
      { status: 500 }
    );
  }
}

app/api/generate-blog-idea/route.ts.content:
import { NextResponse } from "next/server";
import { generateBlogIdea } from "@/server/openai";

export async function POST(req: Request) {
  try {
    const { topic, category } = await req.json();

    if (!topic) {
      return NextResponse.json(
        { message: "Topic is required" },
        { status: 400 }
      );
    }

    const blogIdea = await generateBlogIdea(
      topic, 
      category || "SaaS Growth"
    );

    return NextResponse.json({
      success: true,
      blogIdea
    });
  } catch (error: any) {
    console.error("Error generating blog idea:", error);

    if (error.status === 429 || error.code === 'insufficient_quota') {
      return NextResponse.json(
        { 
          message: "AI content generator temporarily unavailable",
          code: "SERVICE_UNAVAILABLE"
        },
        { status: 503 }
      );
    }

    return NextResponse.json(
      { 
        message: "Failed to generate blog idea",
        error: error.message 
      },
      { status: 500 }
    );
  }
}

app/api/qualify-lead/route.ts.content:
import { NextResponse } from "next/server";
import { storage } from "@/server/storage";
import { qualifyLead } from "@/server/openai";

export async function POST(req: Request) {
  try {
    const { name, email, company, helpWith, message } = await req.json();

    if (!name || !email || !helpWith) {
      return NextResponse.json(
        { message: "Name, email, and help needed are required" },
        { status: 400 }
      );
    }

    const submission = await storage.createContactSubmission({
      name,
      email,
      company: company || null,
      helpWith,
      message: message || null,
      aiCategory: null,
      aiPriority: null,
      aiResponse: null,
    });

    try {
      const qualification = await qualifyLead(name, email, company, helpWith, message);

      await storage.updateContactSubmission(submission.id, {
        aiCategory: qualification.category,
        aiPriority: qualification.priority,
        aiResponse: qualification.suggestedResponse,
      });

      return NextResponse.json({
        success: true,
        saved: true,
        qualification
      });
    } catch (aiError: any) {
      console.error("AI qualification failed, but submission was saved:", aiError);

      return NextResponse.json({
        success: true,
        saved: true,
        aiUnavailable: true,
        message: "Your inquiry was saved successfully. We'll respond within 24 hours."
      });
    }
  } catch (error: any) {
    console.error("Error processing contact submission:", error);
    return NextResponse.json(
      { 
        message: "Failed to save your inquiry. Please try again or contact us directly.",
        error: error.message 
      },
      { status: 500 }
    );
  }
}

